html {
    background-image: linear-gradient(-170deg,
     #064997 20%, #105ba7);
    background-position: right;
}

body {
    min-height: 100vh;
    position: relative;
    font-family: 'Josefin Sans', sans-serif;
    color: var(--colour-white);
    background-image: linear-gradient(to left, #2b67ac 3px, transparent 3px),
                      linear-gradient(#2b67ac 3px, transparent 3px),
                      linear-gradient(to left, rgba(#2b67ac, 0.4) 1px, transparent 1px),
                      linear-gradient(#2b67ac 1px, transparent 1px),
                      linear-gradient(to left, rgba(#2b67ac, 0.4) 1px, transparent 1px),
                      linear-gradient(#2b67ac 1px, transparent 1px);
    background-size: 112px 112px, // 4 * 28 - tried aligning these the grid with getSize but safari was being weird
                     112px 112px,
                     56px 56px, // 2 * 28
                     56px 56px,
                     28px 28px,
                     28px 28px;
}

.page-animate-in {
  animation: fade-in .4s ease-in-out .15s both;
}

@keyframes fade-in {
  0% {
      opacity: 0;
  }
  100% {
      opacity: 1;
  }
}

.page-title {
  font-size: getSize(8);
  text-align: center;
  padding: getSize(6) 0 getSize(6);
}

.home-link {
  color: var(--colour-white);
  font-size: getSize(2);
  text-decoration: none;
  margin-left: getSize(1);
  margin-top: getSize(1);
  height: getSize(4);
  display: inline-flex;
  align-items: center;

  &__icon {
    width: getSize(2);
    fill: var(--colour-white);
    display: inline-block;
    vertical-align: middle;
    margin-top: -0.2em;
    margin-right: getSize(.5);
  }
}

