.links {
    color: var(--colour-black);
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    min-height: 100vh;

    &__list {
        position: relative;
    }

    &__item {
      width: var(--link-size);
      height: var(--link-size);
      margin-top: getSize(2);

      @media (min-width: $breakpoint) {
          position: absolute;
          top: 0;
          left: 0;
          margin-top: calc(var(--link-size) / -2);
          margin-left: calc(var(--link-size) / -2);

          --angle: calc(360deg / var(--item-total));
          --rotation: calc(140deg + var(--angle) * var(--item-count));
          transform:
            rotate(var(--rotation))
            translate(calc(var(--link-size) + #{getSize(2)}))
            rotate(calc(var(--rotation) * -1));
          }
    }


    &__link {
        opacity: 0;
        animation: on-load 300ms ease-in-out forwards;
        animation-delay: calc(var(--item-count) * 150ms);
        width: 100%;
        height: 100%;
        border-radius: 50%;
        position: relative;
        background-color: var(--colour-white);
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-decoration: none;
        color: inherit;

        &:after {
            content: "";
            background-color: transparent;
            width: var(--link-size);
            height: var(--link-size);
            border: 2px dashed var(--colour-white);
            display: block;
            border-radius: 50%;
            position: absolute;
            top: 0;
            left: 0;
            transition: all 0.3s cubic-bezier(0.53,-0.67,0.73,0.74);
            transform: initial;
            opacity: 0;
        }

        &:hover {
            &:after {
                transition: all 0.3s cubic-bezier(0.37,0.74,0.15,1.65);
                transform: scale(1.1);
                opacity: 1;
            }

            .links__icon {
                transition: all 0.3s ease-in-out;
                transform: translateY(getSize(-1));
            }

            .links__text {
                display: block;
            }
        }
    }

    &__icon {
        width: getSize(10);
        height: getSize(10);
        transition: all 0.3s ease-in-out;
        fill: var(--colour-black);

        &--articles,
        &--mail {
          width: getSize(9);
          height: getSize(9);
        }

        &--linkedin {
          width: getSize(8);
          height: getSize(8);
        }
    }

    &__text {
        position: absolute;
        width: 100%;
        bottom: getSize(2);
        left: 0;
        text-align: center;
        height: getSize(2);
        font-size: getSize(2);
        text-align: 0;
        display: block;

        @media (hover) {
          display: none;
          bottom: getSize(8.5);
          animation: text 300ms ease-in-out forwards;
        }
    }
}

@keyframes on-load {
    0% {
        opacity: 0;
        transform: scale(0.3);
    }
    70% {
        opacity: 0.7;
        transform: scale(1.1);
    }
    100% {
        opacity: 1;
        transform: scale(1);
    }
}

@keyframes text {
    0% {
        opacity: 0;
        transform: scale(0.3) translateY(0);
    }
    100% {
        opacity: 1;
        transform: scale(1) translateY(getSize(5));
    }
}

sources.scss/_articles.scss:
.articles {
  $article-size: getSize(35);
  $border-size: 2px;

  max-width: 80%;
  margin: getSize(2) auto getSize(5);

  display: grid;
  grid-row-gap: getSize(8);
  grid-column-gap: getSize(4);
  grid-template-columns: repeat(auto-fit,minmax($article-size,1fr));
  justify-items: center;

  &__article {
    cursor: pointer;
    display: block;
    width: $article-size;
    height: $article-size;
    position: relative;
    perspective: 1000px;
    animation-name: animateIn;
    animation-duration: 350ms;
    animation-delay: calc(var(--animation-order) * 100ms);
    animation-fill-mode: both;
    animation-timing-function: ease-in-out;

    &:before {
      content: "";
      position: absolute;
      top: getSize(-2);
      left: getSize(-2);
      width: $article-size;
      height: $article-size;
      border: $border-size dashed white;
      background-image: repeating-linear-gradient(-24deg, transparent, transparent 4px, rgba(255, 255, 255, 0.5) 4px, rgba(255, 255, 255, 0.5) 5px);
      z-index: -1;
    }
  }

  &__link {
    background-color: var(--colour-blue);
    border: $border-size solid white;
    height: 100%;
    display: block;
    width: 100%;
    height: 100%;

    &:after {
      content: "";
      position: absolute;
      top: 50%;
      right: getSize(3);
      width: getSize(2);
      height: getSize(2);
      margin-top: getSize(-1);
      clip-path: polygon(75% 0%, 100% 50%, 75% 100%, 0% 100%, 25% 50%, 0% 0%);
      -webkit-clip-path: polygon(75% 0%, 100% 50%, 75% 100%, 0% 100%, 25% 50%, 0% 0%);
      background-color: var(--colour-white);
      opacity: 0;
      transition: opacity 500ms ease-in, transform 300ms ease-in-out 0ms;
    }

    @media (hover) {
      &:hover {
        &:after {
          opacity: 1;
          transform: translateX(getSize(1.5));
          transition: opacity 500ms ease-in, transform 300ms ease-in-out 250ms;
        }

        .articles__content--rhs {
          background-color: #F5F5F5;
          transform: rotateY(-50deg);
        }
      }
    }
  }

  &__content {
    background-color: white;
    color: var(--colour-black);
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    font-size: getSize(4);
    line-height: getSize(4.5);
    font-weight: 900;
    letter-spacing: -0.02em;
    padding: getSize(2);
    display: flex;
    flex-direction: column;
    border: $border-size solid white;

    &--lhs {
      clip-path: polygon(0% 0%, 51% 0%, 51% 100%, 0% 100%); // 51% covers any gaps created by pixel rounding
      -webkit-clip-path: polygon(0% 0%, 51% 0%, 51% 100%, 0% 100%);
    }

    &--rhs {
      clip-path: polygon(50% 0%, 100% 0%, 100% 100%, 50% 100%);
      -webkit-clip-path: polygon(50% 0%, 100% 0%, 100% 100%, 50% 100%);
      transition: transform 500ms ease-in-out, background-color 400ms ease-in-out;
    }
  }

  &__footer {
    margin-top: auto;
    font-size: getSize(2);
    line-height: getSize(2);
    display: flex;
    justify-content: space-between;
  }
}

@keyframes animateIn {
  0% {
    opacity: 0;
    transform: scale(0.6) translateY(#{getSize(-1)}) translateX(#{getSize(-1)});
  }

  100% {
    opacity: 1;
  }
}
